import os

from azure.identity import DefaultAzureCredential
from azure.mgmt.batch import BatchManagementClient
from azure.mgmt.storage import StorageManagementClient
from azure.mgmt.resource import ResourceManagementClient


def main():

    SUBSCRIPTION_ID = os.environ.get("SUBSCRIPTION_ID", None)
    GROUP_NAME = "testgroupx"
    ACCOUNT = "accountxxyzxx"
    CERTIFICATE = "SHA1-cff2ab63c8c955aaf71989efa641b906558d9fb7"
    STORAGE_ACCOUNT = "storageaccountxxy"

    # Create client
    # # For other authentication approaches, please see: https://pypi.org/project/azure-identity/
    resource_client = ResourceManagementClient(
        credential=DefaultAzureCredential(),
        subscription_id=SUBSCRIPTION_ID
    )
    batch_client = BatchManagementClient(
        credential=DefaultAzureCredential(),
        subscription_id=SUBSCRIPTION_ID
    )
    # - init depended client -
    storage_client = StorageManagementClient(
        credential=DefaultAzureCredential(),
        subscription_id=SUBSCRIPTION_ID
    )
    # - end -

    # Create resource group
    resource_client.resource_groups.create_or_update(
        GROUP_NAME,
        {"location": "eastus"}
    )

    # - init depended resources -
    # Create storage account
    storage_account = storage_client.storage_accounts.begin_create(
        GROUP_NAME,
        STORAGE_ACCOUNT,
        {
          "sku": {
            "name": "Standard_GRS"
          },
          "kind": "StorageV2",
          "location": "eastus",
          "encryption": {
            "services": {
              "file": {
                "key_type": "Account",
                "enabled": True
              },
              "blob": {
                "key_type": "Account",
                "enabled": True
              }
            },
            "key_source": "Microsoft.Storage"
          },
          "tags": {
            "key1": "value1",
            "key2": "value2"
          }
        }
    ).result()
    print(f"Create storage account:\n{storage_account}")

    # Create account
    account = batch_client.batch_account.begin_create(
        GROUP_NAME,
        ACCOUNT,
        {
            "location": "eastus",
            "auto_storage": {
                "storage_account_id": storage_account.id
            }
        }
    ).result()
    print(f"Create batch account:\n{account}")
    # - end -

    # Create certificate
    certificate = batch_client.certificate.create(
        GROUP_NAME,
        ACCOUNT,
        CERTIFICATE,
        {
            'thumbprint': 'cff2ab63c8c955aaf71989efa641b906558d9fb7',
            'thumbprint_algorithm': 'sha1',
            'data': '',
            'format': 'Pfx',
            'password': 'nodesdk'
        }
    )
    print(f"Create certificate:\n{certificate}")

    # Get certificate
    certificate = batch_client.certificate.get(
        GROUP_NAME,
        ACCOUNT,
        CERTIFICATE
    )
    print(f"Get certificate:\n{certificate}")

    # Update certificate
    certificate = batch_client.certificate.update(
        GROUP_NAME,
        ACCOUNT,
        CERTIFICATE,
        {
            'password': 'nodesdk',
            'data': ''
        }
    )
    print(f"Update certificate:\n{certificate}")

    # Delete certificate
    certificate = batch_client.certificate.begin_delete(
        GROUP_NAME,
        ACCOUNT,
        CERTIFICATE
    ).result()
    print("Delete certificate.\n")

    # Delete Group
    resource_client.resource_groups.begin_delete(
        GROUP_NAME
    ).result()


if __name__ == "__main__":
    main()
